package api;

import java.security.KeyFactory;
import java.security.PrivateKey;
import java.security.spec.PKCS8EncodedKeySpec;
import java.util.Base64;

import javax.crypto.Cipher;

import org.springframework.http.ResponseEntity;
import org.springframework.web.client.RestTemplate;

import algo.RSA;

public class RSAClient {
	private PrivateKey privateKey;

	private static final String PRIVATE_KEY_STRING = "";

	public void initFromStrings() {
		try {
			PKCS8EncodedKeySpec keySpecPrivate = new PKCS8EncodedKeySpec(decode(PRIVATE_KEY_STRING));

			KeyFactory keyFactory = KeyFactory.getInstance("RSA");

			privateKey = keyFactory.generatePrivate(keySpecPrivate);
		} catch (Exception ignored) {
		}
	}

	private static byte[] decode(String data) {
		return Base64.getDecoder().decode(data);
	}

	public String decrypt(String encryptedMessage) throws Exception {
		byte[] encryptedBytes = decode(encryptedMessage);
		Cipher cipher = Cipher.getInstance("RSA/ECB/PKCS1Padding");
		cipher.init(Cipher.DECRYPT_MODE, privateKey);
		byte[] decryptedMessage = cipher.doFinal(encryptedBytes);
		return new String(decryptedMessage, "UTF8");
	}

	public static void main(String[] args) {
		RSA rsa = new RSA();
		rsa.initFromStrings();

		RestTemplate restTemplate = new RestTemplate();
		String fooResourceUrl = "http://localhost:9090/getSecretMessage";
		ResponseEntity<String> response = restTemplate.getForEntity(fooResourceUrl, String.class);

		try {

			String decryptedMessage = rsa.decrypt(response.getBody());
			System.err.println("Decrypted:\n" + decryptedMessage);

		} catch (Exception ingored) {
		}

	}
}